import 'package:blockchain_utils/blockchain_utils.dart';
import 'package:test/expect.dart';
import 'package:test/scaffolding.dart';
import 'package:zcash_dart/src/crypto/src/crypto.dart';
import 'package:zcash_dart/src/pczt/pczt/pczt.dart';

void main() {
  _test();
}

void _test() {
  test("Pczt Serialization", () {
    final pcztHex =
        "";
    final pcztBytes = BytesUtils.fromHexString(pcztHex);
    final pczt = Pczt.deserialize(pcztBytes);
    expect(pczt.encode(), pcztBytes);
  });
  test("Pczt Serialization", () async {
    final pcztHex =
        "";
    final pcztBytes = BytesUtils.fromHexString(pcztHex);
    final pczt = Pczt.deserialize(pcztBytes);
    expect(pczt.encode(), pcztBytes);
    expect(pczt.global.expiryHeight, 10000040);
    final context = DefaultZCashCryptoContext.lazy();
    final tx = await pczt.extract(context);
    expect(tx.transactionData.transparentBundle?.vin.length, 1);
    expect(tx.transactionData.transparentBundle?.vout.length, 2);
    expect(
      tx.transactionData.transparentBundle?.vin[0].sequence,
      BinaryOps.maxUint32,
    );
    expect(
      tx.txId.toTxId(),
      "64ce6536f885034dbff056c57001a08c2d17d63c6e8822fbe6c10f23ee83c5d3",
    );
  });
  test("Pczt Serialization", () async {
    final pcztHex =
        "";
    final pcztBytes = BytesUtils.fromHexString(pcztHex);
    final pczt = Pczt.deserialize(pcztBytes);
    expect(pczt.encode(), pcztBytes);
    expect(pczt.global.expiryHeight, 10000040);
    final context = DefaultZCashCryptoContext.lazy();
    final tx = await pczt.extract(
      context,
      verifyProofs: false,
      verifySignatures: true,
    );
    expect(tx.transactionData.transparentBundle?.vin.length, 1);
    expect(tx.transactionData.orchardBundle?.actions.length, 2);
    expect(
      tx.transactionData.transparentBundle?.vin[0].sequence,
      BinaryOps.maxUint32,
    );
    expect(
      tx.txId.toTxId(),
      "4aa4d0e13bf5f7ed34de521b8fde99f0eeb07ea640bfe17a8dde597a48ff5cd6",
    );
  });

  test("Pczt Serialization Saplig -> Orchard", () async {
    final pcztHex =
        "";
    final pcztBytes = BytesUtils.fromHexString(pcztHex);
    final pczt = Pczt.deserialize(pcztBytes);
    expect(pczt.encode(), pcztBytes);
    final context = DefaultZCashCryptoContext.lazy();
    final tx = await pczt.extract(
      context,
      verifyProofs: false,
      verifySignatures: true,
    );
    expect(
      tx.txId.toTxId(),
      "a54904502a76a8d4be3dc2a08815317592b2dc9998f62ef5bac8183ec4357149",
    );
  });

  test("Pczt Serialization Orchard -> Orchard", () async {
    final pcztHex =
        "";
    final pcztBytes = BytesUtils.fromHexString(pcztHex);
    final pczt = Pczt.deserialize(pcztBytes);
    expect(pczt.encode(), pcztBytes);
    final context = DefaultZCashCryptoContext.lazy();
    final tx = await pczt.extract(
      context,
      verifyProofs: false,
      verifySignatures: true,
    );
    expect(
      tx.txId.toTxId(),
      "97403d040687f8f1dd0a9a78106c5a0c0cdcd1fbe2079756abb32ec3ea81c037",
    );
  });
}
